const certificates = [certificate2024,certificate2023,certificatePRODPILOT];

String situatHost= "unionb.com";
String prodPilotHost= "ubdirect.com";


 bool isTrustedCertificate(X509Certificate? cert,String host){
  return certificates.contains(cert?.pem) && host.contains(certificateHost) && cert!.endValidity.isAfter(DateTime.now());
}

String get certificateHost {
  if (FlavorConfig.isLive()) {
    return prodPilotHost;
  } else {
    return situatHost;
  }
}
